  logic [`BRAM_DATA_WIDTH-1:0] bram_din_a, bram_din_b;
  logic [`BRAM_DATA_WIDTH-1:0] bram_dout_a, bram_dout_b;
  logic bram_we_a, bram_we_b;
  bram_6144x128 bram (
                  .addra(bram_addr_a),
                  .clka(clk),
                  .dina(bram_din_a),
                  .douta(bram_dout_a),
                  .wea(bram_we_a),

