                  .clkb(clk),
                  .dinb(bram_din_b),
                  .doutb(bram_dout_b),
                  .web(bram_we_b)
                );

  compress #(
             .N(N)
           )compress(
             .clk(clk),
             .rst_n(rst_n),

             .t0(t0),
             .t1(t1),
             .hm(hm),
             .lower_half(lower_half),
             .valid(valid),
             .last(last),

             .output_addr(bram_addr_a),
             .output_data(bram_din_a),
             .output_we(bram_we_a),

             .accept(accept),
             .reject(reject)
           );

