  logic [`BRAM_ADDR_WIDTH-1:0] bram_addr_b_delayed;
  delay_register #(.BITWIDTH(`BRAM_ADDR_WIDTH), .CYCLE_COUNT(2)) bram_addr_b_delay(.clk(clk), .in(bram_addr_b), .out(bram_addr_b_delayed));

  always #5 clk = ~clk;

  always_ff @(posedge clk or negedge rst_n) begin
    if(bram_out_valid_delayed == 1'b1) begin

