    for (int i = 0; i < N/2; i++) begin
      logic signed [63:0] tmp1, tmp2;
      tmp1 = g[i];
      tmp2 = g[i+N/2];
      instruction = {4'b1000, 3'b000, i[8:0], 3'b000, 9'b000000000, 4'b1100}; // BRAM write; bank1=0; addr1=i; bank2=/; addr2=/; args=write,int_to_double
      bram_din = {tmp1, tmp2};
      #10;
    end
    #10;  // Wait for pipeline to finish
    instruction = 32'b0000_000_000000000_000_000000000_0000; // Stop writing to BRAM
    #10;

    // Load f into BRAM1
    for (int i = 0; i < N/2; i++) begin
      logic signed [63:0] tmp1, tmp2;
      tmp1 = f[i];
      tmp2 = f[i+N/2];
      instruction = {4'b1000, 3'b000, i[8:0], 3'b000, 9'b000000000, 4'b1100}; // BRAM write; bank1=1; addr1=i; bank2=/; addr2=/; args=write,int_to_double
      bram_din = {tmp1, tmp2};
      #10;
    end
    #10;  // Wait for pipeline to finish
    instruction = 32'b0000_000_000000000_000_000000000_0000; // Stop writing to BRAM
    #10;

    // Load G into BRAM2
    for (int i = 0; i < N/2; i++) begin
      logic signed [63:0] tmp1, tmp2;
      tmp1 = G[i];
      tmp2 = G[i+N/2];
      instruction = {4'b1000, 3'b000, i[8:0], 3'b000, 9'b000000000, 4'b1100}; // BRAM write; bank1=2; addr1=i; bank2=/; addr2=/; args=write,int_to_double
      bram_din = {tmp1, tmp2};
      #10;
    end
    #10;  // Wait for pipeline to finish
    instruction = 32'b0000_000_000000000_000_000000000_0000; // Stop writing to BRAM
    #10;

    // Load F into BRAM3
    for (int i = 0; i < N/2; i++) begin
      logic signed [63:0] tmp1, tmp2;
      tmp1 = F[i];
      tmp2 = F[i+N/2];
      instruction = {4'b1000, 3'b000, i[8:0], 3'b000, 9'b000000000, 4'b1100}; // BRAM write; bank1=3; addr1=i; bank2=/; addr2=/; args=write,int_to_double
      bram_din = {tmp1, tmp2};
      #10;
    end
    #10;  // Wait for pipeline to finish
    instruction = 32'b0000_000_000000000_000_000000000_0000; // Stop writing to BRAM
    #10;

