                 .dma_bram_dout(dma_bram_dout),
                 .dma_bram_byte_we(dma_bram_byte_we)
               );

  int i;  // For loading data to BRAM, wont be used in final design
  logic public_key_loaded, signature_loaded, message_loaded;

  always_ff @(posedge clk) begin
    if (!rst_n) begin
      done <= 1'b0;
      running <= 1'b0;
      instruction_index <= 0;
      i <= 0;
      public_key_loaded <= 1'b0;
      signature_loaded <= 1'b0;
      message_loaded <= 1'b0;
    end
    else begin

      if (start == 1'b1)
        running <= 1'b1;

      if(running) begin
        if(!public_key_loaded) begin
          if(i < N/2) begin
            bram_din <= {49'b0, public_key[i], 49'b0, public_key[i + N/2]};
            i <= i + 1;
          end
          else begin
            public_key_loaded <= 1'b1;
            i <= 0;
          end
        end
        else if(!signature_loaded) begin
          if(i < SIGNATURE_BLOCKS/2) begin
            bram_din <= {signature_blocks[i*2], signature_blocks[i*2+1]};
            i <= i + 1;
          end
          else begin
            signature_loaded <= 1'b1;
            i <= 0;
          end
        end
        else if(!message_loaded) begin
          if(i < MESSAGE_BLOCKS) begin
            bram_din <= {64'b0, message_blocks[i]};
            i <= i + 1;
          end
          else begin
            message_loaded <= 1'b1;
            i <= 0;
          end
        end

        // Increment on instruction done, if we're loading data (first 307 instructions)
        if(instruction_index < 307)
          instruction_index <= instruction_index + 1;
        else if(instruction_done)
          instruction_index <= instruction_index + 2;

        if(instruction_done)
          instruction <= 128'b0;
        else
          // instruction <= (algorithm_select == 1'b1) ? verify_instructions[instruction_index] : sign_instructions[instruction_index];
          instruction <= verify_instructions[instruction_index];

        // Done condition for successful verify
        if(instruction_index == 317 && (bram_dout == 128'hffffffffffffffffffffffffffffffff || bram_dout == 128'b0))
          done <= 1'b1;
      end
    end
  end

